mapper.select:
SELECT
USER_NUMB,      /*유저 ID*/
USER_NAME,      /*유저 이름*/
USER_GRAD,      /*유저 학년*/
USER_BIRT,      /*유저 생일*/
USER_STAT,      /*유저 상태
                        // 0 : 자퇴
                        // 1 : 재학
                        // 2 : 휴학
                        // 3 : 졸업*/
USER_MONE,      /*회비 냈는지 안냈는지 여부
                         // 0 : 안냄
                        // 1 : 냄*/
USER_PHON,      /*핸드폰 번호*/
USER_FLAG,      /*기수*/
PASS_WORD       /*비밀번호(관리자만 설정)*/
FROM CuvicUser
